css:
/*Alteração Fonte Barlow Semi Condensed*/
Body {
  font-family: 'Barlow Semi Condensed', sans-serif;
}

/*Barra Fixa de Promoção Topo*/

/* Tablet and desktop */
@media (min-width: 767px) { 
  .header {
  top: 50px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}
/* Mobile */
@media screen and (max-width: 480px) {
  .header {
    top: 29px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}

main .top-bar {
    display: block;
}

main .top-bar__nav {
    font-size: 0px!important;
    padding: 0px!important;
}

@media (min-width: 992px) {
.top-bar__countdown {
    font-size: 15px!important;
}
}

@media (min-width: 767px) {
.header {
    top: 32px!important;
}
}

/*Cor do Barra Menu*/
.header {
  Background: #070706;
}

/*Cor da barra menu carrinho*/
.app__header {
    background: #070706;
}

/*cor icones*/
.header__buttons .btn {
    padding: var(--spacer-2);
    margin-left: var(--spacer-1);
    color: #fff;
    transition: color .2s;
    box-shadow: none;
    position: relative;
}
/*COR ÍCONES CABEÇALHO AO PASSAR O MOUSE*/
.header__buttons .btn:hover {
    color: #ff6600;
}

/*Cor icone Menu*/
.header__toggler-icon {
    color: #fff;
    font-size: 1.3rem;
    margin-right: var(--spacer-1);
    margin-bottom: -.13rem;
}

/*Cor icone Menu AO PASSAR O MOUSE*/
.header__toggler-icon:hover {
    color: #ff6600;
}

/*Cor texto menu*/
main .header__nav a {
    color: #ffffff;
    border-bottom: 2px solid transparent;
}

/*Cor texto telefone carrinho*/
.app__contact span:not(:first-child) {
    color: #f86300;
}

/*Cor texto precisa de ajuda carrinho*/
.app__contact {
    color: #ffffff;
}

/*Cor texto Site seguro*/
.app__safe {
    color: #28a745;
}

/*Fullbanner*/
@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100%!important;
  }
}

/*Tamanho Ícones Clique*/
.stamps img {
    max-width: 100%;
    height: 40px;
    max-height: 55px;
}
