css:
.header {
    border-width: 2px;
    padding: var(--spacer-2) var(--spacer-3);
    background-color: #171819;
}

main .header__nav a {
    color: #f3f3f3;
    border-bottom: 2px solid transparent;
}



element.style {
}
i[class*=" fa-"], i[class^=fa-] {
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    display: inline-block;
    font-style: normal;
    font-variant: normal;
    text-rendering: auto;
    text-decoration: inherit;
    text-transform: none;
    line-height: 1;
    font-family: storefront-fa;
    font-weight: 400;
}
.header__toggler-icon {
    color: #ffffff;
    font-size: 1.3rem;
    margin-right: var(--spacer-1);
    margin-bottom: -.13rem;
}

.header__buttons .btn {
    padding: var(--spacer-2);
    margin-left: var(--spacer-1);
    color: #ffffff;
    transition: color .2s;
    box-shadow: none;
    position: relative;
}

.top-bar {
    background: #ff6600;
    width: 100%;
}

.main .top-bar {
    display: inline;
    flex-direction: column;
}

.main .top-bar__nav {
    font-size: 0%;
}

.top-bar__nav {
    padding: 0px;
}

.app__header {
    background: #171819;
    padding: var(--spacer-2);
    box-shadow: var(--box-shadow-sm);
    text-align: center;
    font-size: var(--font-size-sm);
}

.app__contact {
    color: #f8f9fa;
}

.app__contact span:not(:first-child) {
    color: #f8f9fa;
}

Body {
    margin: 0;
    font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,Noto Sans,sans-serif,Apple Color Emoji,Segoe UI Emoji,Segoe UI Symbol,Noto Color Emoji;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    color: #212529;
    text-align: left;
    background-color: #fff;
}

@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100% !important;
  }
}

// apenas tablet Full banner

@media screen and (min-width: 481px) and (max-width: 991px) {
  .banner-slider {
    max-width: 100% !important;
  }
}

main .top-bar {
    display: inline;
    flex-direction: column;
}

main .top-bar__nav {
    font-size: 0%;
}

.top-bar__nav {
    padding: 0px;
}

// Icon Clique 

.stamps img {
    max-height: 30px;
}

//Tamanho da Fonte Topo promocao

.top-bar a.top-bar__countdown {
    background: var(--primary);
    color: var(--primary-yiq);
    font-size: smaller !importante;
}

//Cor do texto 100%seguro

.app__safe {
    display: inline;
    color: #fff;
}

// Fixar Barra topo Promocao

.top-bar {
    background: #ff6600;
    width: 100%;
    position: fixed;
    z-index: 999;
}

