css:
/*Alteração Fonte Barlow Semi Condensed*/
Body {
  font-family: 'Barlow Semi Condensed', sans-serif;
}

/*Barra Fixa de Promoção Topo*/

/* Tablet and desktop */
@media (min-width: 767px) { 
  .header {
  top: 50px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
  .btn-primary {
    background-color: #00DC00;
    border:none;
  }
  .btn-primary:hover {
    background-color: #009A00;
  }
}
/* Mobile */
@media screen and (max-width: 480px) {
  .header {
    top: 29px;
  }
  .top-bar {
    position: sticky;
    top: 0;
    z-index: 900;
  }
}

main .top-bar {
    display: block;
}

main .top-bar__nav {
    font-size: 0px!important;
    padding: 0px!important;
}

@media (min-width: 992px) {
.top-bar__countdown {
    font-size: 15px!important;
}
}

@media (min-width: 767px) {
.header {
    top: 32px!important;
}
}

/*Cor do Barra Menu*/
.header {
  Background: #070706;
}

/*Cor da barra menu carrinho*/
.app__header {
    background: #070706;
}

/*cor icones*/
.header__buttons .btn {
    padding: var(--spacer-2);
    margin-left: var(--spacer-1);
    color: #fff;
    transition: color .2s;
    box-shadow: none;
    position: relative;
}
/*COR ÍCONES CABEÇALHO AO PASSAR O MOUSE*/
.header__buttons .btn:hover {
    color: #ff6600;
}

/*Cor icone Menu*/
.header__toggler-icon {
    color: #fff;
    font-size: 1.3rem;
    margin-right: var(--spacer-1);
    margin-bottom: -.13rem;
}

/*Cor icone Menu AO PASSAR O MOUSE*/
.header__toggler-icon:hover {
    color: #ff6600;
}


/*Cor texto menu Categorias*/
main .header__nav a {
    color: #ffffff;
    border-bottom: 0px solid transparent;
    border-radius: 2px;
}

/*COR FUNDO MENU CATEGORIAS AO PASSAR MOUSE*/
.header__nav a:hover {
    background-color: #ff6600;
}

/*Cor texto telefone carrinho*/
.app__contact span:not(:first-child) {
    color: #f86300;
}

/*Cor texto precisa de ajuda carrinho*/
.app__contact {
    color: #ffffff;
}

/*Cor texto Site seguro*/
.app__safe {
    color: #28a745;
}

/*Fullbanner*/
@media  screen and (min-width: 992px) and (max-width: 1367px) {
  .banner-slider {
    max-width: 100%!important;
  }
}

/*RODAPÉ*/
/*Tamanho Ícones Clique*/
.stamps img {
    max-width: 100%;
    height: 40px;
    max-height: 55px;
}

/*Cor icones redes sociais*/
.footer__social a {
    color:#000!important;
}

/*Remoção do nome Descrição do Produto*/
.lead {
    display: none;
}

/*COR BOTÃO COMPRAR HOVER*/
.btn-primary {
    background-color:#49be63;
    border:none;
}

.btn-primary:hover {
    background-color: #22913b;
}

/*LARGURA BOTÃO COMPRAR*/
.btn-group-lg>.btn, .btn-lg {
    padding: 15px 73px 15px 73px;
}

/*BOTÃO COMPRAR FIXO*/
.product-card__buy.fade {
    opacity: 1!important;
    padding-top: 10px;
    text-transform: uppercase;
    color:#28a745;
}

/*COR BARRA PROGRESSO FRETE*/
.shipping-calculator__free-from-value .progress-bar {
    background-color: #ffc107;
}

/*Diminuir Grid de banners Categorias*/
/*Desktop*/
@media (min-width: 992px){
.col-lg-3 {
    flex: 0 0 20%!important;
    max-width: 20%!important;
}
}
/*Mobile*/
@media(min-width: 767px) {
  .page--home .banners-grid .col-lg-3 {
    max-width: 20% !important
  }
}
.col-12 {
    flex: 0 0 100%;
    max-width: 50% !importantt;
}
